import React, { useState } from "react";
import "./Products.css";

const Products = () => {
  const [isPaused, setIsPaused] = useState(false);

  // Product images (repeat for two rows)
  const productImages = [
    "/sbs_products_1.jpg",
    "/sbs_products_2.jpg",
    "/sbs_products_3.jpg",
    "/sbs_products_1.jpg",
    "/sbs_products_2.jpg",
    "/sbs_products_3.jpg",
  ];

  // Duplicate for seamless looping
  const duplicatedImages = [...productImages, ...productImages];

  return (
    <section id="products" className="products-slider-section">
      <div
        className={`products-slider ${isPaused ? "paused" : ""}`}
        onMouseEnter={() => setIsPaused(true)}
        onMouseLeave={() => setIsPaused(false)}
      >
        {[0, 1].map((row) => (
          <div className="slider-row" key={row}>
            <div className="slider-track">
              {duplicatedImages.map((img, idx) => (
                <div className="slider-image-wrapper" key={row + "-" + idx}>
                  <img src={img} alt="Product" className="slider-image" />
                </div>
              ))}
            </div>
          </div>
        ))}
      </div>
    </section>
  );
};

export default Products;
