import React, { useState, useEffect } from "react";
import "./Products.css";

const Products = () => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const [isPaused, setIsPaused] = useState(false);

  // Placeholder product data
  const productCategories = [
    {
      id: 1,
      title: "·Äõ·ÄΩ·Äæ·Ä±·Äú·ÄÄ·Ä∫·ÄÄ·Ä±·Ä¨·ÄÄ·Ä∫·Äô·Äª·Ä¨·Ä∏",
      englishTitle: "Gold Bracelets",
      description: "·Äô·Äº·Äî·Ä∫·Äô·Ä¨·Äï·ÄØ·Ä∂·ÄÖ·Ä∂ ·Äõ·ÄΩ·Äæ·Ä±·Äú·ÄÄ·Ä∫·ÄÄ·Ä±·Ä¨·ÄÄ·Ä∫·Äô·Äª·Ä¨·Ä∏",
      icon: "üìø",
    },
    {
      id: 2,
      title: "·ÄÖ·Ä≠·Äî·Ä∫·Äú·ÄÄ·Ä∫·ÄÖ·ÄΩ·Äï·Ä∫·Äô·Äª·Ä¨·Ä∏",
      englishTitle: "Diamond Rings",
      description: "·Ä°·Äõ·Ää·Ä∫·Ä°·Äû·ÄΩ·Ä±·Ä∏·Äô·Äº·ÄÑ·Ä∑·Ä∫ ·ÄÖ·Ä≠·Äî·Ä∫·Äú·ÄÄ·Ä∫·ÄÖ·ÄΩ·Äï·Ä∫·Äô·Äª·Ä¨·Ä∏",
      icon: "üíç",
    },
    {
      id: 3,
      title: "·Äõ·ÄΩ·Äæ·Ä±·Äú·Ää·Ä∫·ÄÄ·Äº·Ä≠·ÄØ·Ä∏·Äô·Äª·Ä¨·Ä∏",
      englishTitle: "Gold Necklaces",
      description: "·ÄÅ·Ä±·Äê·Ä∫·Äô·ÄÆ ·Äõ·ÄΩ·Äæ·Ä±·Äú·Ää·Ä∫·ÄÄ·Äº·Ä≠·ÄØ·Ä∏·Äô·Äª·Ä¨·Ä∏",
      icon: "üìø",
    },
    {
      id: 4,
      title: "·Äî·Ä¨·Ä∏·ÄÄ·Äï·Ä∫·Äô·Äª·Ä¨·Ä∏",
      englishTitle: "Earrings",
      description: "·Äú·Äæ·Äï·Äû·Ä±·Ä¨ ·Äî·Ä¨·Ä∏·ÄÄ·Äï·Ä∫·Äô·Äª·Ä¨·Ä∏",
      icon: "üëÇ",
    },
    {
      id: 5,
      title: "·Äú·ÄÄ·Ä∫·Äù·Äê·Ä∫·Äõ·Äê·Äî·Ä¨ ·ÄÖ·ÄØ·Ä∂·Äú·ÄÑ·Ä∫",
      englishTitle: "Jewelry Sets",
      description: "·Äô·ÄΩ·Ä±·Ä∏·Äú·Ä¨·Äò·ÄΩ·Ä¨·Ä∏·Ä°·Äê·ÄΩ·ÄÄ·Ä∫ ·Äú·ÄÄ·Ä∫·Äù·Äê·Ä∫·Äõ·Äê·Äî·Ä¨·ÄÖ·ÄØ·Ä∂·Äô·Äª·Ä¨·Ä∏",
      icon: "üíé",
    },
    {
      id: 6,
      title: "·Ä°·Äô·Äª·Ä≠·ÄØ·Ä∏·Äû·Ä¨·Ä∏ ·Äú·ÄÄ·Ä∫·Äù·Äê·Ä∫·Äõ·Äê·Äî·Ä¨",
      englishTitle: "Men's Jewelry",
      description: "·Ä°·Äô·Äª·Ä≠·ÄØ·Ä∏·Äû·Ä¨·Ä∏·Äô·Äª·Ä¨·Ä∏·Ä°·Äê·ÄΩ·ÄÄ·Ä∫ ·Äú·ÄÄ·Ä∫·Äù·Äê·Ä∫·Äõ·Äê·Äî·Ä¨·Äô·Äª·Ä¨·Ä∏",
      icon: "‚åö",
    },
  ];

  useEffect(() => {
    if (!isPaused) {
      const slideInterval = setInterval(() => {
        setCurrentSlide((prev) => (prev + 1) % productCategories.length);
      }, 5000); // Changed from 3000ms to 5000ms (5 seconds)

      return () => clearInterval(slideInterval);
    }
  }, [productCategories.length, isPaused]);

  const nextSlide = () => {
    setCurrentSlide((prev) => (prev + 1) % productCategories.length);
  };

  const prevSlide = () => {
    setCurrentSlide(
      (prev) => (prev - 1 + productCategories.length) % productCategories.length
    );
  };

  return (
    <section id="products" className="products section">
      <div className="container">
        <div className="section-title">
          <h2 className="myanmar-text-bold">·ÄÄ·Äª·ÄΩ·Äî·Ä∫·ÄØ·Äï·Ä∫·Äê·Ä≠·ÄØ·Ä∑·Åè ·Äë·ÄØ·Äê·Ä∫·ÄÄ·ÄØ·Äî·Ä∫·Äô·Äª·Ä¨·Ä∏</h2>
          <p className="english-text">Our Products</p>
        </div>

        <div className="products-slideshow">
          <div className="slideshow-container">
            <div
              className="slides-wrapper"
              style={{ transform: `translateX(-${currentSlide * 100}%)` }}
            >
              {productCategories.map((product, index) => (
                <div key={product.id} className="slide">
                  <div className="product-card">
                    <div className="product-image-placeholder">
                      <div className="product-icon">{product.icon}</div>
                      <div className="coming-soon-overlay">
                        <p className="myanmar-text">·Äì·Ä¨·Äê·Ä∫·Äï·ÄØ·Ä∂·Äô·Äª·Ä¨·Ä∏ ·Äô·ÄÄ·Äº·Ä¨·Äô·ÄÆ</p>
                        <p className="english-text">Photos Coming Soon</p>
                      </div>
                    </div>
                    <div className="product-info">
                      <h3 className="myanmar-text-bold">{product.title}</h3>
                      <h4 className="english-text">{product.englishTitle}</h4>
                      <p className="myanmar-text">{product.description}</p>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>

          <button className="slide-btn prev-btn" onClick={prevSlide}>
            ‚Äπ
          </button>
          <button className="slide-btn next-btn" onClick={nextSlide}>
            ‚Ä∫
          </button>

          <div className="slide-indicators">
            {productCategories.map((_, index) => (
              <button
                key={index}
                className={`indicator ${
                  index === currentSlide ? "active" : ""
                }`}
                onClick={() => setCurrentSlide(index)}
              />
            ))}
          </div>
        </div>

        <div className="products-grid">
          {productCategories.map((product) => (
            <div key={product.id} className="product-grid-item">
              <div className="product-icon-large">{product.icon}</div>
              <h4 className="myanmar-text-medium">{product.title}</h4>
              <p className="english-text">{product.englishTitle}</p>
            </div>
          ))}
        </div>

        <div className="products-cta">
          <p className="myanmar-text">
            ·Äï·Ä≠·ÄØ·Äô·Ä≠·ÄØ·Äû·Ä≠·Äõ·Äæ·Ä≠·Äú·Ä≠·ÄØ·Äï·Ä´·ÄÄ ·ÄÄ·Äª·ÄΩ·Äî·Ä∫·ÄØ·Äï·Ä∫·Äê·Ä≠·ÄØ·Ä∑·Äë·Ä∂ ·ÄÜ·ÄÄ·Ä∫·Äû·ÄΩ·Äö·Ä∫·Äï·Ä´
          </p>
          <button className="btn btn-primary myanmar-text-medium">
            ·ÄÜ·ÄÄ·Ä∫·Äû·ÄΩ·Äö·Ä∫·Äõ·Äî·Ä∫
          </button>
        </div>
      </div>
    </section>
  );
};

export default Products;
